mapper:


	SELECT * FROM file_info WHERE file_group=#{id} order by file_num


<select id="file_s  resultType="map"    parameterType="int">
	SELECT * FROM file_info WHERE t_id=#{id} order by file_group, file_num




	SELECT * FROM file_info WHERE file_id=#{id}



		delete from file_info  where file_id = #{id}


		insert into file_info  ( t_id,file_group,file_num,file_path,file_size,file_img,file_remark)  values (#{t_id},#{file_group},#{file_num},#{file_path},#{file_size},#{file_img},#{file_remark})
